import JSelectDepartModal from './modal/JSelectDepartModal';
export default {
  name: 'JSelectDepart',
  components: {
    JSelectDepartModal: JSelectDepartModal
  },
  props: {
    modalWidth: {
      type: Number,
      default: 500,
      required: false
    },
    multi: {
      type: Boolean,
      default: false,
      required: false
    },
    rootOpened: {
      type: Boolean,
      default: true,
      required: false
    },
    value: {
      type: String,
      required: false
    },
    disabled: {
      type: Boolean,
      required: false,
      default: false
    },
    // 自定义返回字段，默认返回 id
    customReturnField: {
      type: String,
      default: 'id'
    },
    backDepart: {
      type: Boolean,
      default: false,
      required: false
    }
  },
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      departNames: "",
      departIds: ''
    };
  },
  mounted: function mounted() {
    this.departIds = this.value;
  },
  watch: {
    value: function value(val) {
      //update-begin-author:wangshuai date:20201124 for:组件 JSelectDepart.vue不是默认id时新内容编辑问题 gitee I247X2
      // if (this.customReturnField === 'id') {
      this.departIds = val;
      // }
      //update-end-author:wangshuai date:20201124 for:组件 JSelectDepart.vue不是默认id时新内容编辑问题 gitee I247X2
    }
  },
  methods: {
    initComp: function initComp(departNames) {
      var _this = this;
      this.departNames = departNames;
      //update-begin-author:lvdandan date:20200513 for:TESTA-438 部门选择组件自定义返回值，数据无法回填
      //TODO 当返回字段为部门名称时会有问题,因为部门名称不唯一
      //返回字段不为id时，根据返回字段获取id
      if (this.customReturnField !== 'id' && this.value) {
        var dataList = this.$refs.innerDepartSelectModal.dataList;
        console.log('this.value', this.value);
        this.departIds = this.value.split(',').map(function (item) {
          var data = dataList.filter(function (d) {
            return d[_this.customReturnField] === item;
          });
          return data.length > 0 ? data[0].id : '';
        }).join(',');
      }
      //update-end-author:lvdandan date:20200513 for:TESTA-438 部门选择组件自定义返回值，数据无法回填
    },
    //返回选中的部门信息
    backDeparInfo: function backDeparInfo() {
      if (this.backDepart === true) {
        if (this.departIds && this.departIds.length > 0) {
          var arr1 = this.departIds.split(',');
          var arr2 = this.departNames.split(',');
          var info = [];
          for (var i = 0; i < arr1.length; i++) {
            info.push({
              value: arr1[i],
              text: arr2[i]
            });
          }
          this.$emit('back', info);
        }
      }
    },
    openModal: function openModal() {
      this.$refs.innerDepartSelectModal.show();
    },
    handleOK: function handleOK(rows, idstr) {
      var _this2 = this;
      var value = '';
      if (!rows && rows.length <= 0) {
        this.departNames = '';
        this.departIds = '';
      } else {
        value = rows.map(function (row) {
          return row[_this2.customReturnField];
        }).join(',');
        this.departNames = rows.map(function (row) {
          return row['departName'];
        }).join(',');
        this.departIds = idstr;
      }
      this.$emit("change", value);
      this.backDeparInfo();
    },
    getDepartNames: function getDepartNames() {
      return this.departNames;
    },
    handleEmpty: function handleEmpty() {
      this.handleOK('');
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
};
