import SubjectForm from "./SubjectForm";
export default {
  name: 'SubjectModal',
  components: {
    SubjectForm: SubjectForm
  },
  data: function data() {
    return {
      title: '',
      width: 400,
      visible: false,
      disableSubmit: false
    };
  },
  methods: {
    edit: function edit(model, type) {
      var _this = this;
      this.visible = true;
      // 这个方法可以等待dom更新后，执行
      this.$nextTick(function () {
        _this.$refs.realForm.edit(model, type);
      });
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      this.$refs.realForm.submitForm();
    },
    submitCallback: function submitCallback() {
      this.$emit('ok');
      this.visible = false;
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
