import { httpAction, getAction } from '@/api/manage';
import { validateDuplicateValue } from '@/utils/util';
import ATextarea from "ant-design-vue/es/input/TextArea";
export default {
  name: 'KmSysConfigForm',
  components: {
    ATextarea: ATextarea
  },
  props: {
    //表单禁用
    disabled: {
      type: Boolean,
      default: false,
      required: false
    }
  },
  data: function data() {
    return {
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {},
      url: {
        add: "/KM/kmSysConfig/add",
        edit: "/KM/kmSysConfig/edit",
        queryById: "/KM/kmSysConfig/queryById"
      }
    };
  },
  computed: {
    formDisabled: function formDisabled() {
      return this.disabled;
    }
  },
  created: function created() {
    //备份model原始值
    this.modelDefault = JSON.parse(JSON.stringify(this.model));
  },
  methods: {
    add: function add() {
      this.edit(this.modelDefault);
    },
    edit: function edit(record) {
      this.model = Object.assign({}, record);
      this.visible = true;
    },
    submitForm: function submitForm() {
      var _this = this;
      var that = this;
      // 触发表单验证
      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';
          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }
          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
          });
        }
      });
    }
  }
};
