import { httpAction, getAction } from '@/api/manage';
import JTreeSelect from '@/components/jeecg/JTreeSelect';
export default {
  name: "SysCategoryModal",
  components: {
    JTreeSelect: JTreeSelect
  },
  data: function data() {
    return {
      title: "操作",
      width: 800,
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        pid: {},
        name: [{
          required: true,
          message: '请输入类型名称!'
        }]
      },
      url: {
        add: "/sys/category/add",
        edit: "/sys/category/edit",
        checkCode: "/sys/category/checkCode"
      },
      expandedRowKeys: [],
      pidField: "pid",
      subExpandedKeys: []
    };
  },
  created: function created() {},
  computed: {
    disabled: function disabled() {
      return this.model.id ? true : false;
    }
  },
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      this.model = Object.assign({}, record);
      this.visible = true;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk: function handleOk() {
      var _this = this;
      var that = this;
      // 触发表单验证
      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';
          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }
          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.submitSuccess(_this.model);
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    submitSuccess: function submitSuccess(formData) {
      if (!formData.id) {
        var treeData = this.$refs.treeSelect.getCurrTreeData();
        this.expandedRowKeys = [];
        this.getExpandKeysByPid(formData[this.pidField], treeData, treeData);
        if (formData.pid && this.expandedRowKeys.length == 0) {
          this.expandedRowKeys = this.subExpandedKeys;
        }
        this.$emit('ok', formData, this.expandedRowKeys.reverse());
      } else {
        this.$emit('ok', formData);
      }
    },
    getExpandKeysByPid: function getExpandKeysByPid(pid, arr, all) {
      if (pid && arr && arr.length > 0) {
        for (var i = 0; i < arr.length; i++) {
          if (arr[i].key == pid) {
            this.expandedRowKeys.push(arr[i].key);
            this.getExpandKeysByPid(arr[i]['parentId'], all, all);
          } else {
            this.getExpandKeysByPid(pid, arr[i].children, all);
          }
        }
      }
    }
  }
};
