import { httpAction } from '@/api/manage';
export default {
  name: "SysMessageTestModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      url: {
        send: "/sys/message/sysMessageTemplate/sendMsg"
      },
      templateName: "",
      templateContent: "",
      receiver: "",
      msgType: "",
      testData: "",
      sendParams: {}
    };
  },
  methods: {
    open: function open(record) {
      this.sendParams.templateCode = record.templateCode;
      this.templateName = record.templateName;
      this.templateContent = record.templateContent;
      this.testData = record.templateTestJson;
      this.visible = true;
    },
    close: function close() {
      this.receiver = "";
      this.msgType = "";
      this.sendParams = {};
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this = this;
      var httpurl = this.url.send;
      var method = 'post';
      this.sendParams.testData = this.testData;
      this.sendParams.receiver = this.receiver;
      this.sendParams.msgType = this.msgType;
      httpAction(httpurl, this.sendParams, method).then(function (res) {
        if (res.success) {
          _this.$message.success(res.message);
        } else {
          _this.$message.warning(res.message);
        }
      }).finally(function () {
        _this.confirmLoading = false;
        _this.close();
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
