import moment from 'moment';
import { getAction } from '@/api/manage';
moment.locale('zh-cn');
export default {
  data: function data() {
    return {
      time: '',
      loading: true,
      tableLoading: true,
      columns: [{
        title: '参数',
        width: '30%',
        dataIndex: 'param',
        scopedSlots: {
          customRender: 'param'
        }
      }, {
        title: '描述',
        width: '40%',
        dataIndex: 'text',
        scopedSlots: {
          customRender: 'text'
        }
      }, {
        title: '当前值',
        width: '30%',
        dataIndex: 'value',
        scopedSlots: {
          customRender: 'value'
        }
      }],
      dataSource: [],
      // 列表通过 textInfo 渲染出颜色、描述和单位
      textInfo: {
        'system.cpu.count': {
          color: 'green',
          text: 'CPU 数量',
          unit: '核'
        },
        'system.cpu.usage': {
          color: 'green',
          text: '系统 CPU 使用率',
          unit: '%'
        },
        'process.start.time': {
          color: 'purple',
          text: '应用启动时间点',
          unit: ''
        },
        'process.uptime': {
          color: 'purple',
          text: '应用已运行时间',
          unit: '秒'
        },
        'process.cpu.usage': {
          color: 'purple',
          text: '当前应用 CPU 使用率',
          unit: '%'
        }
      },
      // 当一条记录中需要取出多条数据的时候需要配置该字段
      moreInfo: {}
    };
  },
  mounted: function mounted() {
    this.loadTomcatInfo();
  },
  methods: {
    handleClickUpdate: function handleClickUpdate() {
      this.loadTomcatInfo();
    },
    loadTomcatInfo: function loadTomcatInfo() {
      var _this = this;
      this.tableLoading = true;
      this.time = moment().format('YYYY年MM月DD日 HH时mm分ss秒');
      Promise.all([getAction('actuator/metrics/system.cpu.count'), getAction('actuator/metrics/system.cpu.usage'), getAction('actuator/metrics/process.start.time'), getAction('actuator/metrics/process.uptime'), getAction('actuator/metrics/process.cpu.usage')]).then(function (res) {
        var info = [];
        res.forEach(function (value, id) {
          var more = _this.moreInfo[value.name];
          if (!(more instanceof Array)) {
            more = [''];
          }
          more.forEach(function (item, idx) {
            var param = value.name + item;
            var val = value.measurements[idx].value;
            if (param === 'system.cpu.usage' || param === 'process.cpu.usage') {
              val = _this.convert(val, Number);
            }
            if (param === 'process.start.time') {
              val = _this.convert(val, Date);
            }
            info.push({
              id: param + id,
              param: param,
              text: 'false value',
              value: val
            });
          });
        });
        _this.dataSource = info;
      }).catch(function (e) {
        console.error(e);
        _this.$message.error('获取服务器信息失败');
      }).finally(function () {
        _this.loading = false;
        _this.tableLoading = false;
      });
    },
    convert: function convert(value, type) {
      if (type === Number) {
        return Number(value * 100).toFixed(2);
      } else if (type === Date) {
        return moment(value * 1000).format('YYYY-MM-DD HH:mm:ss');
      }
      return value;
    }
  }
};
